def get_icon(name: str, color: str = "#E5E5E5", size: int = 16) -> str:
    """Get an SVG icon with custom color and size."""
    icon = ICONS.get(name, ICONS["alert"])
    icon = icon.replace('width="16"', f'width="{size}"')
    icon = icon.replace('height="16"', f'height="{size}"')
    icon = icon.replace('stroke="currentColor"', f'stroke="{color}"')
    return icon


def icon_button_html(icon_name: str, color: str = "#E5E5E5", size: int = 18) -> str:
    """Create inline icon HTML for embedding in markdown."""
    icon = get_icon(icon_name, color, size)
    return f'<span style="display: inline-flex; align-items: center; vertical-align: middle;">{icon}</span>'
